import React,{useState} from "react";

const currentTime = new Date().toLocaleTimeString();

const[curTime , setTime]= useState(new Date().toLocaleTimeString());

setTime(new Date().toLocaleTimeString());

setInterval(setTime,1000);

function Time(){

    return <div>
    <h2>{currentTime}</h2>
    </div>
};
export default Time;
