import React,{useState} from "react";

const currentTime = new Date().toLocaleTimeString();

const[curTime , setTime]= useState(currentTime);

function updateTime(){
 const time =new Date().toLocaleTimeString();

 setTime(time)

};
setInterval(updateTime,1000)




function Time(){

    return <div>
    <h2>{curTime}</h2>
    </div>
};
export default Time;
